sys_script_include.script:
var GetEmailAddres = Class.create();
GetEmailAddres.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getEmail: function() {
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('sysparm_userID'));
		return userRecord.email + '';
	},

    type: 'GetEmailAddres'
});
